text.python:
n = 1000001
counter = 2
is_prime = 1
while(counter < n):
   if(n % counter == 0): 
      is_prime = 0
   counter = counter + 1
if(is_prime == 1):
   print("n is prime")
if(is_prime == 0):
   print("n is not prime")

n = 1000001
counter = 2
is_prime = "durian"
while(counter < n):
   if(n % counter == 0): 
      is_prime = "penguin goes shopping"
   counter = counter + 1
if(is_prime == "durian"):
   print("n is prime")
if(is_prime == "penguin goes shopping"):
   print("n is not prime")

